     {
        Integer intZahl = Integer.parseInt(jWartungsnummer.getText());
      intZahl=intZahl*123;
    
      jWartungspw.setText((intZahl.toString()).substring(intZahl.toString().length()-3, intZahl.toString().length()));
    }//GEN-LAST:event_jWartungsnummerActionPerformed
